import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Assuming you are using axios for HTTP requests

const ToggleButton = () => {
  const [theme, setTheme] = useState('light'); // initial value can be fetched from server

  useEffect(() => {
    axios.get('/api/getTheme').then(response => {
      setTheme(response.data.theme);
    });
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    // Update the theme on the server
    axios.post('http://127.0.0.1:8000/updateTheme/', { theme: newTheme }) // Replace with your API endpoint
      .then(response => {
        // Update local state
        setTheme(newTheme);
      })
      .catch(error => {
        console.error('Error updating theme:', error);
      });
  };

  return (
    <div>
      <button onClick={toggleTheme}>
        Toggle to {theme === 'light' ? 'Dark' : 'Light'} Theme
      </button>
    </div>
  );
};

export default ToggleButton;
