import { BrowserRouter, Route, Routes, useNavigate } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import Login from './pages/Login';
import Register from './pages/Register'
import Home from './pages/Home'
import RegistrationComplete from './pages/RegistrationComplete';
import EditProfile from './pages/EditProfile';


const RoutesWrapper = ({ setToken }) => {

  const navigate = useNavigate();

  if (['/register', '/registration-complete'].includes(location.pathname)) {
    return;
  }

  useEffect(() => {

    const storedToken = localStorage.getItem('access_token');

    if(storedToken == null) {

      setToken(null);
      console.log("missing token! Redirect called");  // Debugging line
      navigate('/'); // Redirect to login page if no token is found

    } else {

      setToken(storedToken);
      console.log("Token found! Redirect called");  // Debugging line
      navigate('/home'); // Redirect to home page if token is found
    }

  }, [navigate]);

  return (
    <Routes>
      <Route path="/" element={<Login setToken={setToken} />} />
      <Route path="/register" element={<Register />} />
      <Route path="/home" element={<Home />} />
      <Route path="/registration-complete" element={<RegistrationComplete />} />
      <Route path="/edit-profile" element={<EditProfile />} />
    </Routes>
  );
};

const App = () => {

  // eslint-disable-next-line
  const [_, setToken] = useState(null);

  return (
    <BrowserRouter>
      <RoutesWrapper setToken={setToken} />
    </BrowserRouter>
  );
};



export default App;
