import React, { useEffect } from 'react';
import { useTheme } from './ThemeContext';  // Import your useTheme hook
import axios from 'axios';

const ToggleButton = () => {
  const { theme, toggleTheme } = useTheme(); // Get the current theme and toggle function

  // Effect to set the :root attribute based on the theme
  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);

    axios.post('/api/theme', { theme })
    
  }, [theme]);

  return (
    <div>
      <button onClick={toggleTheme}>
        Toggle Theme
      </button>
    </div>
  );
};

export default ToggleButton;
