import axios from "axios"
import { useNavigate } from 'react-router-dom';
import React, { useState, useEffect } from 'react';

import ToggleButton from '../components/ToggleButton';

const Home = () => {

  const navigate = useNavigate();  // Moved this line here
  const [username, setUsername] = useState('Unknown');
  const [first_name, setFirstName] = useState('');
  const [last_name, setLastName] = useState('');
  const [is_activated, setIsActivated] = useState('Not Verified'); // New state variable

  const edit_profile = () => {
    navigate('/edit-profile');  // Using an absolute path
    console.log("Edit Profile button clicked");
  };  
  
  useEffect(() => { 
    const token = localStorage.getItem('access_token');
    if (!token) {
      console.log("missing token! Redirect called");  // Debugging line
      navigate('/'); // Redirect to login page if no token is found
    }
  }, [navigate]);

  useEffect(() => {
    // Fetch account status
    axios.get('http://127.0.0.1:8000/get-account-data/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('access_token')}`
      }
    })
    .then((response) => {
      if (response.status === 200) {
        setIsActivated(response.data.is_activated ? 'Verified' : 'Not Verified');
        setUsername(response.data.username); // Assuming the response contains a username field
        setFirstName(response.data.first_name); // Assuming the response contains a first_name field
        setLastName(response.data.last_name); // Assuming the response contains a last_name field

        console.log("This is the response: ", response);
      }
    })
    .catch((error) => {
      console.error("There was an error fetching account data!", error);
      setIsActivated('Error');
    });

    console.log("Home page loaded");  // Debugging line
  }, []);

  const sign_out = async () => {

    axios.post('http://127.0.0.1:8000/logout/', {}, {headers: {
      'Authorization': `Bearer ${localStorage.getItem('access_token')}`
    }})
    .then((response) => {
      console.log("This is the response: ", response);
      if (response.status === 200) {
        console.log(response.data.access)
        localStorage.removeItem("access_token");
        redirect();
      }
    })
    .catch((error) => {
      console.error("There was an error!", error);
      localStorage.removeItem("access_token");
      console.log("Token: ", localStorage.getItem("access_token"), " Cleared")
      redirect();
    });

    console.log("Signout called");  // Debugging line

  };

  const redirect = () => {
    console.log("Redirect called");  // Debugging line
    navigate('/');
  }

  const verify_account = async () => {

    axios.post('http://127.0.0.1:8000/resend-activation-email/', {
      username,
    }, {
      
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('access_token')}`
      }})

    .then((response) => {
      console.log("This is the response: ", response);
      if (response.status === 200) {
        console.log(response.data.access)
        localStorage.removeItem("access_token");
        redirect();
      }
    })
     
  }

  const delete_account = () => {

    navigate('/delete-account');  // Using an absolute path

  }

  // const getToken = () => {
  //   console.log("this is the token", localStorage.getItem("access_token"))
  // }

  // const clearToken = () => {
  //   console.log("Token: ", localStorage.getItem("access_token"), " Cleared")
  //   localStorage.removeItem("access_token")
  //   navigate('/')
  // }

  return (
    <div>
      <div id='container'>
        <h1>{first_name} "{username}" {last_name}</h1>
        <h2>Account Status: {is_activated}</h2>
        <button onClick={sign_out} className='signout'>Sign Out</button>
        <button onClick={edit_profile}>Edit Profile</button>
        {is_activated !== 'Verified' && <button onClick={verify_account}>Verify Account</button>}
        <button onClick={delete_account}>Delete Account</button>
        {/* <button onClick={getToken}>Get Token</button> */}
        {/* <button onClick={clearToken}>Clear Token</button> */}
        <ToggleButton/>
        {/* <CheckAuthButton/> */}
      </div>
    </div>
  )
}

export default Home;
