import Cookies from 'js-cookies';

export const setThemeCookie = (theme) => {
  Cookies.set('theme', theme, { expires: 365 }); // setting the theme in a cookie
};

export const getThemeCookie = () => {
  return Cookies.get('theme'); // getting the theme from a cookie
};
