import React, { createContext, useContext, useState, useEffect } from 'react';
import { getThemeCookie, setThemeCookie } from './helper';

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState('light');  // Default theme set to 'light'

  // Function to toggle between light and dark theme
  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);

    // Call to a helper function to store the current theme in a cookie
    setThemeCookie(newTheme);
  };

  // Using useEffect to set the theme from the cookie when the component mounts
  useEffect(() => {
    const savedTheme = getThemeCookie();
    if (savedTheme) {
      setTheme(savedTheme);
    }
  }, []);  // Empty dependency array means this useEffect runs once when the component mounts

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

// Your helper functions to set and get theme cookies can go here
